import React, { useState } from 'react';
import { StyleSheet, View, Text, ImageBackground, TextInput, TouchableOpacity } from 'react-native';
import { useNavigation } from '@react-navigation/native';

import bg from '../assets/bg.png';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';

const CustomScreen = ({ navigation, route }) => {

    const { username } = route.params;
    console.log(username)

    const [userinput, setCustom] = useState('');
    const [error, setError] = useState('');

    const setCustomGoal = () => {
        // Check if userinput is empty
        if (!userinput) {
            setError('Please enter your drinking goal.');
            return;
        }

        // Clear any previous error
        setError('');

        // Pass the custom goal to the Dashboard screen
        navigation.navigate('Dashboard', { username, customGoal: userinput });
    };

    const goBack = () => {
        navigation.goBack();
    };

    return (
        <ImageBackground source={bg} style={styles.container}>
            <View style={styles.overlay}>
                <TouchableOpacity style={styles.backButton} onPress={goBack}>
                    <MaterialIcons name="arrow-back" style={styles.backButtonIcon} />
                </TouchableOpacity>

                <View style={styles.inputContainer}>
                    <View style={styles.inputBox}>
                        <TextInput
                            style={[styles.textInput, !userinput && styles.placeholder]}
                            placeholder="Enter your Drinking Goal"
                            onChangeText={(text) => {
                                text = text.replace(/[^0-9]/g, '').slice(0, 4);
                                setCustom(text);
                            }}
                            value={userinput}
                            keyboardType="numeric"
                        />
                        <Text style={styles.mlText}>ML</Text>
                    </View>
                    <TouchableOpacity style={styles.addButton} onPress={setCustomGoal}>
                        <Text style={styles.addButtonLabel}>Finish</Text>
                    </TouchableOpacity>

                    {error ? <Text style={styles.errorText}>{error}</Text> : null}
                </View>
            </View>
        </ImageBackground>
    );
};

export default CustomScreen;

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },

    overlay: {
        flex: 1,
        backgroundColor: 'rgba(0, 0, 0, 0.4)',
    },

    backButton: {
        position: 'absolute',
        top: 30,
        left: 20,
        padding: 10,
        zIndex: 1,
    },

    backButtonIcon: {
        fontSize: 24,
        color: 'white',
    },

    inputContainer: {
        alignItems: 'center',
        top: 300,
        marginBottom: 30,
        paddingHorizontal: 70,
        paddingVertical: 20,
    },

    inputBox: {
        flexDirection: 'row',
        borderBottomWidth: 2,
        borderColor: 'white',
        alignItems: 'center',
    },

    textInput: {
        flex: 1,
        fontSize: 20,
        color: 'white',
        paddingVertical: 10,
    },

    // Enter your drinking goal
    placeholder: {
        color: 'gray',
    },

    // ml
    mlText: {
        color: 'white',
        fontSize: 18,
    },

    addButton: {
        backgroundColor: '#8BADD3',
        paddingVertical: 23,
        paddingHorizontal: 50,
        borderRadius: 10,
        top: 130,
        alignItems: 'center',
    },

    addButtonLabel: {
        fontSize: 18,
        color: '#333',
    },

    // Error message text
    errorText: {
        color: 'red',
        marginTop: 10,
    },
});
